mapper.select:
SELECT issueSeq AS ISSUESEQ , title AS TITLE , workSeq AS WORKSEQ , workName AS WORKNAME , projectSeq AS PROJECTSEQ , projectName AS PROJECTNAME , TO_CHAR(regDate, 'YYYY-MM-DD') AS REGDATE , TO_CHAR(hopeDate, 'YYYY-MM-DD') AS HOPEDATE , TO_CHAR(manageDate, 'YYYY-MM-DD') AS MANAGEDATE , content AS CONTENT , imergency AS IMERGENCY , iType AS ITYPE , iStateSeq AS ISTATESEQ , IRoleSeq AS IROLESEQ FROM TBLISSUE WHERE employeeseq = #{employeeseq}
AND TITLE LIKE '%'||#{title}||'%'
AND IMERGENCY = #{imergency}
order by MANAGEDATE desc, IMERGENCY
