mapper.select:
select o.outputseq as outputseq, o.version as version, f.fileseq as fileseq, f.title as title, w.name as wname, p.name as name, ot.type as type, o.regdate as regdate from tblProject p inner join tblStage s on p.projectSeq = s.projectSeq inner join tblWork w on s.stageSeq = w.stageSeq inner join tblOutput o on w.workSeq = o.workSeq inner join tblFile f on o.outputSeq = f.outputSeq inner join tblOType ot on o.oTypeSeq = ot.oTypeSeq where o.oTypeSeq in (1, 2, 3, 4, 6) and teamseq in (select teamseq from tblTeam where employeeSeq = #{employeeseq})
and s.projectseq = #{projectseq}
and o.otypeseq = #{otypeseq}
and f.title like '%'||#{title}||'%'
