mapper.select:
select p.name as name, p.pcode as pcode, pt.type as type, p.startdate as startdate, p.finishdate as finishdate, t.name as team, vi.issucnt as issuecnt, vo.outputcnt as outputcnt, ps.state as pstate, rnd.state as state, p.projectseq as projectseq from tblteam t inner join tblEmployee e on t.employeeseq = e.employeeseq inner join tblProject p on p.projectseq = t.projectseq inner join tblrnd rnd on rnd.rndseq = p.rndseq inner join tblrole r on r.roleseq = t.roleseq inner join tblpState ps on ps.pstateseq = p.pstateseq inner join tblptype pt on pt.ptypeseq = p.ptypeseq left outer join vdashissue vi on t.name = vi.name left outer join voutputcount vo on t.name = vo.name where r.role='PL'
and ps.state = #{pstate}
and t.name = #{team}
order by p.projectseq

select issueSeq AS ISSUESEQ , title AS TITLE , workSeq AS WORKSEQ , workName AS WORKNAME , projectSeq AS PROJECTSEQ , projectName AS PROJECTNAME , TO_CHAR(regDate, 'YYYY-MM-DD') AS REGDATE , TO_CHAR(hopeDate, 'YYYY-MM-DD') AS HOPEDATE , TO_CHAR(manageDate, 'YYYY-MM-DD') AS MANAGEDATE , content AS CONTENT , imergency AS IMERGENCY , iType AS ITYPE , iStateSeq AS ISTATESEQ , IRoleSeq AS IROLESEQ from TBLISSUE where projectSeq = #{projectseq}
AND TITLE LIKE '%'||#{title}||'%'
AND IMERGENCY = #{imergency}
order by MANAGEDATE desc, IMERGENCY

select p.name as name, p.projectSeq as projectseq, w.name as wname, o.*, f.* from tblProject p inner join tblStage s on p.projectSeq = s.projectSeq inner join tblWork w on s.stageSeq = w.stageSeq inner join tblOutput o on w.workSeq = o.workSeq inner join tblFile f on o.outputSeq = f.outputSeq where o.oTypeSeq in (1, 2, 3, 4, 6) and p.projectSeq = #{projectseq}
and title like '%'||#{title}||'%'
order by o.regdate desc, w.name asc

mapper.update:
update tblOutput
set workSeq=#{workseq},oTypeSeq=#{otypeseq}, fileExplain=#{fileexplain}
	where outputSeq=#{outputseq}
